import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { EventModel } from 'src/app/models/event-model';
import { EventTypeModel } from 'src/app/models/event-type-model';
import { DataService } from 'src/app/services/data.service';

@Component({
  selector: 'app-add',
  templateUrl: './add.component.html',
  styleUrls: ['./add.component.css']
})
export class AddComponent {
    public event = new EventModel();
    public eventTypes:EventTypeModel[] = [];

    public constructor(private dataService: DataService, private router: Router) {}

    public async ngOnInit() {
        try {
            this.eventTypes = await this.dataService.getAllEventTypes();
        }
        catch (err: any) {
            alert(err.message);
        }
    }

    public async send() {
        try{
            await this.dataService.addEvent(this.event)
            alert("Event has been successfully added");
            this.router.navigateByUrl("/list");
        }
        catch (err: any){
            alert(err.message)
        }
    }
}
<form (ngSubmit)="send()" ngNativeValidate>

    <label for="">Event Type:</label>
    <select name="theaterId" [(ngModel)]="event.eventTypeId" required>
        <option selected disabled value="">Select Theater</option>
        <option *ngFor="let et of eventTypes" value={{et._id}}>{{et.name}}</option>
    </select>

    <label for="">Time & Date:</label>
    <input type="datetime-local" name="eventDateTime" [(ngModel)]="event.eventDateTime" required>

    <label for="">Description:</label>
    <input type="text" name="eventDescription" [(ngModel)]="event.eventDescription" required minlength="2" maxlength="100">

    <label for="">Location:</label>
    <input type="text" name="eventLocation" [(ngModel)]="event.eventLocation" required minlength="2" maxlength="100">

    <label for="">Event Capacity:</label>
    <input type="number" name="eventCapacity" [(ngModel)]="event.eventCapacity" required min="5" max="1000">

    <button>Add</button>

</form>
