import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { ContinentModel } from 'src/app/models/continent-model';
import { CountryModel } from 'src/app/models/country-model';
import { DataService } from 'src/app/services/data.service';

@Component({
  selector: 'app-add',
  templateUrl: './add.component.html',
  styleUrls: ['./add.component.css'],
})
export class AddComponent {
  public country = new CountryModel();
  public continents: ContinentModel[] = [];

  public constructor(
    private dataService: DataService,
    private router: Router
  ) {}

  public async ngOnInit() {
    try {
      this.continents = await this.dataService.getContinents();
    } catch (err: any) {
      alert(err.message);
    }
  }

  public async send() {
    try {
      await this.dataService.addCountry(this.country);
      alert('Country has been successfully added');
      this.router.navigateByUrl('/list');
    } catch (err: any) {
      alert(err.message);
    }
  }
}
<form (ngSubmit)="send()" ngNativeValidate>
  <label for="">Continent:</label>
  <select name="continentId" [(ngModel)]="country.continentId" required>
    <option selected disabled value="">Select Theater</option>
    <option *ngFor="let c of continents" value="{{ c._id }}">
      {{ c.name }}
    </option>
  </select>

  <label for="">name:</label>
  <input type="text" name="name" [(ngModel)]="country.name" required />

  <label for="">Included:</label>
  <input
    type="text"
    name="included"
    [(ngModel)]="country.included"
    required
    minlength="2"
    maxlength="100"
  />

  <label for="">Expected:</label>
  <input
    type="text"
    name="expected"
    [(ngModel)]="country.expected"
    required
    minlength="2"
    maxlength="100"
  />

  <label for="">Tip Amount:</label>
  <input
    type="text"
    name="amount"
    [(ngModel)]="country.amount"
    required
    minlength="2"
    maxlength="100"
  />

  <label for="">Who Gets the Tip:</label>
  <input
    type="text"
    name="whoGets"
    [(ngModel)]="country.whoGets"
    required
    minlength="2"
    maxlength="100"
  />

  <button>Add</button>
</form>
